mapper.select:
select
column_name as columnName, column_default as columnDefault,
is_nullable as nullable ,data_type as dataType,character_maximum_length as characterMaximumLength,
column_type as columnType,column_key as columnKey,column_comment as columnComment
from information_schema.columns
where table_schema=#{dbName}
and table_name=#{tbName}
order by ordinal_position;

mapper.update:
update from connection_info
set
ciName=#{ciName},ciUrl=#{ciUrl},ciPort=#{ciPort},ciUser=#{ciUser},
ciPwd=#{ciPwd},ciEtc=#{ciEtc},uID=#{uID}
where ciNo=#{ciNo}
